class Solution {
private:
    vector<vector<vector<int>>> dp;
    int MOD = 1e9 + 7;
    int f(int index, int n, int min_take_1, int max_take_2, vector<int>& nums) {
        if(index >= n) return 1;
        if(dp[index][min_take_1][max_take_2] != -1) return dp[index][min_take_1][max_take_2];
        int ans = 0;
        for(int i = min_take_1; i<=nums[index]; i++) {
            int rest_max_for_arr2 = nums[index] - i;
            if(rest_max_for_arr2 <= max_take_2) 
                ans = (ans + f(index + 1, n, i, rest_max_for_arr2, nums)) % MOD;
        }
        return dp[index][min_take_1][max_take_2] = ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
        int arr_1_min = 0;
        int arr_2_max = nums[0];
        return f(0, n, arr_1_min, arr_2_max, nums);
    }
};
