typedef long long int ll;
#define M 1000000007
int dp[2001][51][51];

int func(int ind, int inc, int dec, vector<int> &a){
    if(ind >= a.size()){
        return 1;
    }
    if(dp[ind][inc][dec] != -1){
        return dp[ind][inc][dec];
    }
    ll ans = 0;
    for(int i = 0; i <= a[ind]; i++){
        if(i >= inc && (a[ind]-i) <= dec)
            ans = (ans + func(ind+1, i, a[ind]-i, a))%M;
    }
    return dp[ind][inc][dec]=ans;
}

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll ans = 0;
        memset(dp,-1,sizeof(dp));
        for(int i = 0; i <= nums[0]; i++){
            ans = (ans + func(1, i, nums[0]-i, nums))%M;
        }
        return ans;
    }
};
