MOD = 10**9 + 7

class Solution:
    def countOfPairs(self, nums: list[int]) -> int:
        n = len(nums)
        dp = [[0] * 51 for _ in range(n+1)]

        # Base case for the first element
        for x in range(nums[0] + 1):
            y = nums[0] - x
            dp[1][x] = 1

        # Fill the DP table
        for i in range(2, n + 1):
            for x in range(nums[i-1] + 1):
                for y in range(x + 1):  # Ensuring arr1 is non-decreasing and arr2 is non-increasing
                    if y <= nums[i-2] and nums[i-1] - x <= nums[i-2] - y:
                        dp[i][x] = (dp[i][x] + dp[i-1][y]) % MOD

        # Result is the sum of dp[n][x] for all x
        return sum(dp[n]) % MOD
