class Solution
{
private:
    const int M = 1e9 + 7;
    int solve(vector<int> &nums, int i, int p1, int p2, vector<vector<vector<int>>> &dp)
    {
        if (i == nums.size())
        {
            return 1;
        }
        if (dp[i][p1][p2] != -1)
        {
            return dp[i][p1][p2];
        }
        int cnt = 0;
        for (int a1i = p1; a1i <= nums[i]; a1i++)
        {
            int a2i = nums[i] - a1i;
            if (a2i <= p2)
            {
                cnt = (cnt + solve(nums, i + 1, a1i, a2i, dp)) % M;
            }
        }
        return dp[i][p1][p2] = cnt;
    }

public:
    int countOfPairs(vector<int> &nums)
    {
        int n = 0;
        n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(nums, 0, 0, 50, dp);
    }
};
