class Solution {
public:
    int dp[2000][51];
    int mod=1e9+7;

    int solve(int i, int j, vector<int>&a, int n){
        if(i==n) return 1;

        if(dp[i][j]!=-1) return dp[i][j];

        long long ans=0;
        for(int k=j;k<=a[i];k++){
            if(i==0 || (a[i-1]-j)>=(a[i]-k))
            ans+=solve(i+1,k,a,n);
            ans%=mod;
        }

        return dp[i][j]=ans;
    }

    int countOfPairs(vector<int>& a) {
        int n=a.size();
        memset(dp,-1,sizeof(dp));
        return solve(0,0,a,n);
    }
};
