class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        @cache
        def recur(minForArr1, maxForArr2, ind):
            if ind == len(nums): return 1
            n = nums[ind]
            res = 0
            for arr1Val in range(max(minForArr1, n-maxForArr2), n+1):
                arr2Val = n-arr1Val
                pairs = recur(arr1Val, arr2Val, ind+1)
                res += pairs
                # print(f'for the {ind} index, with arr1val: {arr1Val} and arr2val: {arr2Val}, there are {pairs} monotonic pairs')
                res %= (10**9 + 7)
            return res

        return recur(0, float('inf'), 0)
