class Solution {
public:
    long long int dp[51][51][2001];
    long long int mod = 1e9+7;
    long long int help(int i,int prev1,int prev2,vector<int> &nums){
        if(i==nums.size()) return 1ll;
        if(dp[prev1][prev2][i]!=-1) return dp[prev1][prev2][i];
        long long int ans=0;
        for(int j=prev1;j<=nums[i];++j){
            int nj = nums[i]-j;
            if(nj<=prev2) ans = (ans%mod + help(i+1,j,nj,nums)%mod)%mod;
        }
        return dp[prev1][prev2][i]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        for(int i=0;i<51;++i){
            for(int j=0;j<51;++j){
                for(int k=0;k<=nums.size();++k) dp[i][j][k]=-1;
            }
        }
        return help(0,0,50,nums);
    }
};
