class Solution {
public:
    int mod = 1e9+7;
    vector<vector<vector<int>>>dp;
    int f(int i, int prev1, int prev2, vector<int>&nums){
        if(i==nums.size())return 1;
        if(dp[i][prev1][prev2]!=-1)return dp[i][prev1][prev2];
        int ways=0;
        for(int k=prev1;k<=nums[i];k++){
            if(nums[i]-k>prev2)continue;
            ways = (ways + f(i+1,k,nums[i]-k,nums))%mod; 
        }
        return dp[i][prev1][prev2] = ways;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp.resize(n,vector<vector<int>>(51,vector<int>(51,-1)));
        return f(0,0,nums[0],nums);
    }
};
