class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> memo(n, vector<int>(51, -1));

        auto dp = [&](auto& dp, int i, int p) -> int {
            if (i == nums.size()) {
                return 1;
            }
            if (memo[i][p] != -1) {
                return memo[i][p];
            }
            int lim = nums[i];
            int ans = 0;
            for (int k = p; k <= nums[i]; ++k) {
                int a = k;
                int b = nums[i] - a;

                if (i == 0 || (a >= p && nums[i - 1] - p >= b)) {
                    ans = (ans + dp(dp, i + 1, a)) % MOD;
                }
            }
            return memo[i][p] = ans;
        };

        return dp(dp, 0, 0);
    }
};
