class Solution {
      const long long int mod = 1e9+7;
      long long dp[60][60][2004];
      long long fun(vector<int>&nums,int n,int pre1,int pre2){
          if(n < 0) return 1;
          if(dp[pre1][pre2][n] != -1) return dp[pre1][pre2][n];
          long long ans = 0;
          for(int i=nums[n];i>=0;i--){
              int val1 = i;
              int val2 = nums[n] - val1;
              if(val1<=pre1 and val2>=pre2) (ans+=fun(nums,n-1,val1,val2))%=mod;
          }
          return dp[pre1][pre2][n] = ans;
      }
public:
    int countOfPairs(vector<int>& nums) {
        int n  = nums.size();
        memset(dp,-1,sizeof(dp));
        return fun(nums,n-1,57,0);
    }
};
