#define MOD 1000000007
#define ll long long

class Solution {
    int st[2001][51][51];
public:
    ll count_pairs(int idx, int p1, int p2, const vector<int>& nums)
    {
        int n = nums.size();
       // cout<<idx<<" "<<p1<<" "<<p2<<"\n";
        if(idx >= n)
            return 1;

        if(st[idx][p1][p2] != -1)
            return st[idx][p1][p2];

        ll ans = 0;
        int m = nums[idx];
        for(int i=0; i<=m; i++)
        {
            int x = i;
            int y = m-i;

            if(x >= p1 && y <= p2)
                ans = (ans%MOD + count_pairs(idx+1, x, y, nums)%MOD)%MOD;
        }

        return st[idx][p1][p2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        memset(st, -1, sizeof(st));

        return count_pairs(0, 0, 50, nums);
    }
};
