class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        dp = [[[0] * 51 for _ in range(51)] for _ in range(n)]

        for i in range(nums[0] + 1):
            dp[0][i][nums[0] - i] = 1

        for i in range(1, n):
            for j in range(51):
                for k in range(51):
                    if dp[i-1][j][k] > 0:
                        for x in range(j, nums[i] + 1):
                            y = nums[i] - x
                            if y <= k:
                                dp[i][x][y] = (dp[i][x][y] + dp[i-1][j][k]) % MOD

        result = 0
        for j in range(51):
            for k in range(51):
                result = (result + dp[n-1][j][k]) % MOD

        return result
