class Solution {
public:
const int mod = 1e9 + 7;
    int recursion(int i, int j, int k, vector<int>&nums, vector<vector<vector<int>>>&dp)
    {
        if(i>=nums.size())
        {
            return 1;
        }
        if(dp[i][j][k]!=-1)
        {
            return dp[i][j][k];
        }
        long long ans=0;
        for(int curr=j;curr>=0;curr--)
        {
             if(curr<=nums[i] && (nums[i]-curr)>=k)
             {
                 ans+=recursion(i+1,curr,nums[i]-curr,nums,dp);
                 ans%=mod;
             }
        }
        return dp[i][j][k]=(int)ans;
    }
    int countOfPairs(vector<int>& nums) {
      int n=nums.size();
      vector<vector<vector<int>>>dp(n,vector<vector<int>>(55,vector<int>(52,-1)));
      int ans=recursion(0,50,0,nums,dp); 
        return ans;
    }
};
