class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
    int MOD = 1e9 + 7;
    vector<vector<int>> dp(n, vector<int>(101, 0)); 

    for (int j = 0; j <= nums[0]; j++) {
        dp[0][j] = 1;
    }

    for (int i = 1; i < n; i++) {
        for (int j = 0; j <= nums[i]; j++) {
            for (int k = 0; k <= j; k++) {
                if (nums[i] - j >= 0 && nums[i] - j <= nums[i - 1] - k) { 
                    dp[i][j] = (dp[i][j] + dp[i - 1][k]) % MOD;
                }
            }
        }
    }

    int count = 0;
    for (int j = 0; j <= 100; j++) {
        count = (count + dp[n - 1][j]) % MOD;
    }

    return count;
    }
};
