class Solution {
public:
    int MOD = 1e9+7;
    int help(vector<int>& nums, int idx, int prev_arr1, int prev_arr2,vector<vector<vector<int>>>& dp){
        int n = nums.size();
        if(idx==n) return 1;
        if(dp[idx][prev_arr1][prev_arr2]!=-1) return dp[idx][prev_arr1][prev_arr2];
        long long output = 0;
        if(idx==0){
            for(int i=0;i<=nums[idx];i++){
                output += help(nums,idx+1,nums[idx]-i,i,dp);
                output %= MOD;
            }
        }
        else{
            for(int i=0;i<=nums[idx];i++){
                if(nums[idx]-i>=prev_arr1 && i<=prev_arr2){
                    output += help(nums,idx+1,nums[idx]-i,i,dp);
                    output %= MOD;
                }
            }
        }
        return dp[idx][prev_arr1][prev_arr2] = output%MOD;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(51,vector<int>(51,-1)));
        return help(nums,0,0,0,dp);
    }
};
