class Solution {
public:
    int mod=1e9+7;
    int help(vector<int>&nums, int i, int p1, int p2, vector<vector<vector<int>>>&v){
        if(i==nums.size()){
            return 1;
        }
        if(v[i][p1+1][p2]!=-1){
            return v[i][p1+1][p2]%mod;
        }
        int ans=0;
        for(int j=p2;j>=0;j--){
            int g=nums[i]-j;
            if(g<p1 || g<0)continue;
            ans=(ans+help(nums,i+1,g,j,v)%mod)%mod;
        }
        return v[i][p1+1][p2]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int maxi=*max_element(nums.begin(),nums.end());
        int n=nums.size();
        vector<vector<vector<int>>>v(n+1,vector<vector<int>>(52, vector<int>(52,-1)));
        return help(nums,0,-1,maxi,v)%mod;
    }
};
