class Solution {
private:
    int MOD = 1e9 + 7;
    vector<vector<vector<int>>> dp;

    int f(int ind, int p1, int p2, vector<int>& nums) {
        int n = nums.size();

        if (ind == n) {
            return 1;
        }

        if (dp[ind][p1+1][p2+1] != -1) {
            return dp[ind][p1+1][p2+1];
        }

        int ways = 0;

        for (int k = 0; k <= nums[ind]; k++) {
            int a1 = k;
            int a2 = nums[ind] - k;

            if ((p1 == -1 || a1 >= p1) && (p2 == -1 || a2 <= p2)) {
                ways = (ways + f(ind + 1, a1, a2, nums)) % MOD;
            }
        }

        return dp[ind][p1+1][p2+1] = ways;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(52, vector<int>(52, -1)));
        return f(0, -1, -1, nums);
    }
};
