public class Solution {
    public int CountOfPairs(int[] nums) {
        long mod = (long)Math.Pow(10,9)+7;
        long res = 0;
        long[,] dp = new long[51,nums.Length];
        for(int i=0;i<nums.Length;i++){
            for(int j=0;j<=nums[i];j++){
                if(i==0) dp[j,i]=1;
                else{
                    for(int k=0;k<=j;k++){
                        if(nums[i-1]-k>= nums[i]-j) dp[j,i]= (dp[j,i]+dp[k, i-1])%mod;
                    }
                }
            }
        }
        for(int i=0;i<51;i++){
            res= (res+dp[i,nums.Length-1])%mod;
        }
        return (int)(res%mod);
    }
}
