class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int[][] dp = new int[n + 1][51];
        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= 50; j++) {
                dp[i][j] = 0;
            }
        }
        dp[0][0] = 1;
        int mod = (int) 1e9 + 7;
        for (int i = 1; i <= n; i++) {
            for (int k = 0; k <= 50; k++) {
                if (dp[i - 1][k] == 0) continue;
                for (int j = 0; j <= nums[i - 1]; j++) {
                    if (i == 1) {
                        dp[i][j] += dp[i - 1][k];
                        dp[i][j] %= mod;
                    }
                    else {
                        if (j >= k && nums[i - 1] - j <= nums[i - 2] - k) {
                            dp[i][j] += dp[i - 1][k];
                            dp[i][j] %= mod;
                        }
                    }
                }
            }
        }
        int ans = 0;
        for (int i = 0; i <= 50; i++) {
            ans = (ans + dp[n][i]) % mod;
        }
        return ans;
    }
}
