int mod = 1e9 + 7;
int dp[51][2001];
int n;
class Solution {
public:
    int solve(int i, int a1, vector<int>& nums) {
        if(i == n){
            return 1;
        }
        if(dp[a1][i] != -1) return dp[a1][i];

        int ans = 0;
        int la2 = i > 0 ? nums[i - 1] - a1 : 101;
        int cnum = nums[i];
        for(int j = a1 ; j <= cnum; ++j) {
          int na2 = cnum - j;
          if(na2 >=0 && la2 >= na2) {
             ans = (ans + solve(i + 1, j, nums)) % mod;
          }
        }

        return dp[a1][i] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        memset(dp, -1, sizeof(dp));
        return solve(0,0, nums);
    }
};
