class Solution {
    public int mod = (int)1e9+7;
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        Integer [][][]dp =new Integer[n+1][53][53];
        return solve(0, 0,51, nums, dp);
    }
    public int solve(int i, int f, int s, int []nums, Integer [][][]dp){
        if(i>=nums.length)return 1;
        if(dp[i][f][s]!=null)return dp[i][f][s];
        long ans=0;
        for(int cur=f;cur<=nums[i];cur++){
            int r = nums[i]-cur;
            if(r<=s){
                ans =(ans+solve(i+1, cur, r, nums,dp))%mod;
            }
        }
        return dp[i][f][s]=(int)(ans%mod);
    }
}
