import java.util.Arrays;

class Solution {
    public static void main(String[] args) {
        System.out.println(new Solution().countOfPairs(new int[]{2, 3, 2})); //4
        System.out.println(new Solution().countOfPairs(new int[]{5, 5, 5, 5})); //126
    }

    public int countOfPairs(int[] nums) {

        int[][][] mem = new int[nums.length][52][52];
        for (var i : mem) {
            for (var j : i) Arrays.fill(j, -1);
        }
        return helper(nums, 0, 0, 51, mem);
    }

    static int mod = 1_000_000_007;

    int helper(int[] nums, int ptr, int preA, int preB, int[][][] mem) {
        if (ptr == nums.length) {
            return 1;
        }
        if (mem[ptr][preA][preB] != -1) return mem[ptr][preA][preB];

        int ans = 0;
        for (int i = 0; i <= nums[ptr]; i++) {
            if (preA <= i && nums[ptr] - i <= preB) {
                ans += helper(nums, ptr + 1, i, nums[ptr] - i, mem);
                ans %= mod;
            }
        }
        return mem[ptr][preA][preB] = ans;
    }
}
