class Solution {
public:
    using ll = long long int;
    const ll MOD = (ll)1e9 + 7;
    ll dp[2005][55];

    ll go(int i, int prev, int n, vector<int> &nums) {
        if(i >= n) return 1;
        if(dp[i][prev] != -1) return dp[i][prev];
        ll ans = 0;
        for(int curr = prev; curr <= nums[i]; curr ++) {
            ll prev2 = i > 0 ? nums[i - 1] - prev : 100;
            ll curr2 = nums[i] - curr;
            if(prev2 >= curr2) {
                ans += go(i + 1, curr, n, nums);
                ans %= MOD;
            }
        }
        return dp[i][prev] = ans % MOD;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp, -1, sizeof(dp));
        ll ans = go(0, 0, n, nums) % MOD;
        return ans % MOD;
    }
};


