const long long mod = 1e9 + 7;
class Solution {
public:

    long long solve(vector<int> &nums, long long idx, long long prev1, long long prev2, vector<vector<vector<long long>>> &dp){
        if(idx >= nums.size()){
            return 1LL;
        }
        if(dp[idx][prev1][prev2] != -1){
            return dp[idx][prev1][prev2];
        }
        long long res = 0;
        for(long long i=prev1; i<=nums[idx]; i++){
            if(prev2 >= nums[idx]-i){
                res = (res + solve(nums,idx+1,i,min(prev2,nums[idx]-i),dp)) % mod;
            }
        }
        return dp[idx][prev1][prev2] = res % mod;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<long long>>> dp(n+1, vector<vector<long long>>(51, vector<long long>(51, -1)));
        return solve(nums,0,0,nums[0],dp);
    }
};
