typedef long long ll;
class Solution {
    ll MOD = 1000000007;
public:
     ll go(int l, int& x, int& y, vector<int>& nums, vector<vector<vector<ll>>>& dp) {
        if(l == nums.size())    return 1;
         if(dp[l][x][y] != -1)  return dp[l][x][y];
        ll ans = 0;
        for(int i = 0; i <= nums[l]; i++) {
            int xx = i, yy = nums[l] - i;
            if(xx >= x and yy <= y)
                ans += go(l+1, xx, yy, nums, dp);
            ans %= MOD;
        }
        return dp[l][x][y] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        ll ans = 0, n = nums.size();
        vector<vector<vector<ll>>> dp(n+1, vector<vector<ll>> (51, vector<ll> (51, -1)));
        for(int i = 0; i <= nums[0]; i++) {
            int x = nums[0] - i;
            ans += go(1, i, x, nums, dp);
            ans %= MOD;
        }
        return ans;
    }
};
