class Solution {
public:
    int MOD = 1e9 + 7;

    int HereWeGoAgain(int i, int prev1, int prev2, vector<int>& nums, vector<vector<vector<int>>>& dp){
        if (i == nums.size()) return 1;

        if (dp[i][prev1][prev2] != -1) return dp[i][prev1][prev2];

        int ways = 0;
        for (int x = prev1; x <= nums[i]; ++x){
            int y = nums[i] - x;
            if (y <= prev2){
                ways = (ways + HereWeGoAgain(i+1, x, y, nums, dp)) % MOD;
            }
        }

        return dp[i][prev1][prev2] = ways;
    }

    int countOfPairs(vector<int>& nums){
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(101, vector<int>(101, -1)));

        return HereWeGoAgain(0, 0, nums[0], nums, dp);
    }
};
