class Solution {
public:
    vector<vector<vector<int>>> dp;
    int mod=1e9+7;
    int f(vector<int>&v,int i,int p,int q){
        if(i==v.size()) return 1;
        if(dp[i][p][q]!=-1) return dp[i][p][q];
        int ans=0;
        for(int a=p;a<=v[i];++a){
            if((v[i]-a)<=q){
                ans=(ans+f(v,i+1,a,v[i]-a))%mod;
            }
        }
        return dp[i][p][q]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        dp.resize(nums.size(),vector<vector<int>>(101,vector<int>(101,-1)));
        return f(nums,0,0,100);
    }
};
