class Solution {
public:

    int t[2001][52][52];
    int mod = 1e9 + 7;

    int Solve(vector<int>&A, int i, int lastInc, int lastDec){
        if(i==A.size()) return 1;

        int Ans = 0 ;

        if(t[i][lastInc][lastDec]!=-1)
            return t[i][lastInc][lastDec];

        for(int j=lastInc; j<=A[i]; j++){
            int req = A[i]-j;
            if(req <= lastDec)
                Ans = (Ans%mod + Solve(A, i+1, j, req)%mod)%mod;
        }

        return t[i][lastInc][lastDec] = Ans%mod; 
    }

    int countOfPairs(vector<int>& nums) {
        memset(t,-1,sizeof(t));
        return Solve(nums, 0, 0, 51);
    }
};
