class Solution {
public:
    int mod = 1e9 + 7;
    int dp[2001][52];
    int solve(int ind , int last , vector<int>&nums){
          if(ind==nums.size()) return 1;
          if(dp[ind][last+1]!=-1) return dp[ind][last+1];

          int ans = 0;

          for(int i=max(last,0);i<=nums[ind];i++){
                 int second = nums[ind] - i;

                 if(ind==0){
                     ans +=solve(ind+1,i,nums);
                     ans%=mod;
                 }
                 else {
                     if(second <= nums[ind-1] - last) ans += solve(ind+1,i,nums);
                     ans %=mod;
                 }
          }
        return dp[ind][last+1] =  ans;
    }
    int countOfPairs(vector<int>& nums) {
          memset(dp,-1,sizeof(dp));
          return solve(0,-1,nums);
    }
};
