class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        @cache
        def solve(a1, idx):
            if idx == len(nums): return 1
            a2 = 50
            if idx != 0: a2 = nums[idx-1] - a1
            ans = 0
            for x in range(a1, 51):
                y = nums[idx] - x
                if x >= a1 and  y <= a2 and x >= 0 and y >= 0:
                    ans += solve(x, idx+1)
            return ans % MOD
        return solve(0, 0)
