class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int m = nums.size();
        long long MOD = 1e9 + 7;
        vector<vector<long long>> dp(m, vector<long long>(51, 0));
        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;
        }
        for (int i = 1; i < m; ++i) {
            for (int j = 0; j <= nums[i]; ++j) {
                for (int k = 0; k <= j; ++k) {
                    if (nums[i] - j <= nums[i - 1] - k) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][k]) % MOD;
                    }
                }
            }
        }
        long long ans = 0;
        for (int j = 0; j <= nums[m - 1]; ++j) {
            ans = (ans + dp[m - 1][j]) % MOD;
        }
        return ans;
    }
};
