class Solution {
public:
    int mod=1e9+7;
    int dp[2001][51][51];
    int Helper(int ind,int p1,int p2,vector<int>& nums){
        if(ind==nums.size())return 1;
        if(dp[ind][p1][p2]!=-1)return dp[ind][p1][p2];

        int ans=0;
        for(int i=p1;i<=nums[ind];i++){
            int x1=i;
            int x2=nums[ind]-i;
            if(x2<=p2){
                ans=(ans+Helper(ind+1,x1,x2,nums)%mod)%mod;
            }
        }
        return dp[ind][p1][p2]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return Helper(0,0,50,nums);
    }
};
