class Solution {
public:
    #define int long long
    const int mod=1e9+7;
    int dp[2001][52];
    int solve(int i,vector<int> &nums,int prev1)
    {
        if(i==nums.size())
        {
            return 1;
        }
        if(dp[i][prev1]!=-1)
        {
            return dp[i][prev1];
        }
        if(i==0)
        {
            //no need to check previous 
            int ans=0;
            for(int j=0;j<=nums[i];j++)
            {
                ans=(ans + solve(i+1,nums,j))%mod;
            }
            return dp[i][prev1]=ans%mod;
        }
        int prev2=nums[i-1]-prev1;
        int ans=0;
        for(int j=0;j<=nums[i];j++)
        {
            int nval2=nums[i]-j;
            if(j>=prev1 && nval2<=prev2)
            {
                ans=(ans+solve(i+1,nums,j))%mod;
            }
        }
        return dp[i][prev1]=ans%mod;
    }
    #undef int
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<=50;j++)
            {
                dp[i][j]=-1;
            }
        }
        vector<long long> Nums(nums.begin(),nums.end());
        return solve(0,Nums,0)%mod;
    }
};
