from typing import List
import functools


class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        inf = float("inf")
        mod = 10 ** 9 + 7

        @functools.lru_cache(None)
        def dp(i, pre1):
            if i == n:
                return 1
            pre2 = inf
            if i > 0:
                pre2 = nums[i - 1] - pre1
            ans = 0
            for x in range(nums[i] + 1):
                v1, v2 = x, nums[i] - x
                if v1 >= pre1 and v2 <= pre2:
                    ans = (ans + dp(i + 1, v1)) % mod
            return ans

        return dp(0, 0)
