class Solution {
    const int MOD = 1e9+7;
    int dp[2001][51][51];
    int solve(int ind, int prev1, int prev2, vector<int> &nums) {
        if(ind == nums.size()) return 1;
        if(dp[ind][prev1][prev2] != -1) return dp[ind][prev1][prev2];
        int ans = 0;
        for(int val1 = prev1; val1 <= nums[ind]; val1++) {
            if(nums[ind]-val1 <= prev2) {
                ans = (ans + solve(ind+1,val1,nums[ind]-val1,nums))%MOD;
            }
        }
        return dp[ind][prev1][prev2] = ans;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp,-1,sizeof(dp));
        return solve(0, 0, nums[0], nums);
    }
};
