class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def dp(ix, a):
            ans = 0
            if ix == n: return 1
            for i in range(a, nums[ix]+1):
                if nums[ix]-i <= (nums[ix-1]-a):
                    ans += dp(ix+1, i)
            return ans
        n = len(nums)
        return sum([ dp(1, k) for k in range(nums[0]+1)])%1000000007
