class Solution {
public:
    const int q=1e9+7;
    vector<vector<vector<int>>>dp;
    int solve(int idx,int num1,int num2,vector<int>& nums){
        if(idx>=nums.size())return 1;
        if(dp[idx][num1][num2]!=-1)return dp[idx][num1][num2];
        int ans=0;
        for(int i=0;i<=nums[idx];i++){
            if(idx==0)ans=((long long)ans+solve(idx+1,i,nums[idx]-i,nums))%q;
            else{
                if(i>=num1&&(nums[idx]-i)<=num2)ans=((long long)ans+solve(idx+1,i,nums[idx]-i,nums))%q;
            }
        }
        return dp[idx][num1][num2]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        dp.resize(n+1,vector<vector<int>>(52,vector<int>(52,-1)));
        return solve(0,0,0,nums);
    }
};
