class Solution {
public:
    long long int mod=1e9+7;

    int doit(int ind, int prea1, vector<int>& nums, vector<vector<int>> &dp, int n){

        if (dp[ind][prea1]!=-1) return dp[ind][prea1];


        if (ind==n){
            return dp[ind][prea1]=1;
        }



        int prea2=INT_MAX;
        if (ind){
            prea2=nums[ind-1]-prea1;
        }

        int tot=0;
        for (int i=0; i<=nums[ind]; i++){
            if (i>=prea1 and nums[ind]-i<=prea2){
                tot = (tot + doit(ind+1, i, nums, dp, n))%mod;
            }
        }

        return dp[ind][prea1]=tot;

    }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>> dp(n+1, vector<int> (1001, -1));

        return doit(0, 0, nums, dp, n);
    }
};
