class Solution {
public:

    long long F[2003][1003];
    long long P[2003][1003];
    long long MOD=1000000007ll;

    void addPrefixSum(int i){
        P[i][0]=F[i][0];
        for(int j=1;j<=1000;++j){
            P[i][j]=(P[i][j-1]+F[i][j])%MOD;
        }
    }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        for(int i=0;i<=1000;++i){
            if(i<=nums[0]) F[0][i]=1;
            else F[0][i]=0;
        }
        addPrefixSum(0);
        for(int i=1;i<nums.size();++i){
            for(int j=0;j<=1000;++j){
                F[i][j]=0ll;
                if(j>nums[i]) continue;
                int maxj=min(j, nums[i-1]-nums[i]+j);
                if(maxj<0) continue;
                F[i][j]=P[i-1][maxj];
            }
            addPrefixSum(i);
        }
        long long ans=0;
        for(int i=0;i<=1000;++i){
            ans=(ans+F[n-1][i])%MOD;
        }
        return (int)ans;
    }
};
