class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> memo(n, vector<int>(51, -1));

        function<int(int, int)> dfs = [&](int index, int prev) {
            if (index == n) return 1;
            if (memo[index][prev] != -1) return memo[index][prev];
            // cout<<"idk"<<index<<" "<<prev<<endl;
            long long count = 0;
            for (int i = prev; i <= nums[index]; i++) {
                if (index == 0 || nums[index] - i <= nums[index-1] - prev) {
                    count = (count + dfs(index + 1, i)) % MOD;
                }
            }
            return memo[index][prev] = count;
        };

        return dfs(0, 0);
    }
};
