class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        @lru_cache(None)
        def dp(index: int, last_arr1_val: int, last_arr2_val: int) -> int:
            # 当所有位置都已处理完毕
            if index == n:
                return 1

            count = 0
            # 遍历 arr1[index] 的可能值
            for arr1_val in range(last_arr1_val, nums[index] + 1):
                arr2_val = nums[index] - arr1_val
                # 确保 arr2_val 不小于上一次的 arr2_val，保持 arr2 单调不增
                if arr2_val <= last_arr2_val:
                    count += dp(index + 1, arr1_val, arr2_val)
                    count %= MOD

            return count

        # 从第一个位置开始，arr1[0] 和 arr2[0] 的初始值为 0 和 nums[0]，以保持初始单调性
        return dp(0, 0, nums[0])
