class Solution {
public:

    const int MOD = 1e9 + 7;

    int countOfPairs(vector<int>& arr) {
        int n = arr.size();
        int dp[n][51][51];
        memset(dp, 0, sizeof dp);

        for (int i = 0; i <= arr[0]; i++)
        {
            int j = arr[0] - i;
            dp[0][i][j] = 1;
        }

        for (int i = 1; i < n; i++)
        {
            for (int j = 0; j <= 50; j++)
            {
                for (int k = 0; k <= 50; k++)
                {
                    if (dp[i - 1][j][k] > 0)
                    {
                        for (int nj = j; nj <= 50; nj++)
                        {
                            int nk = arr[i] - nj;
                            if (nk >= 0 and nk <= k)
                                dp[i][nj][nk] = (dp[i][nj][nk] + dp[i - 1][j][k]) % MOD;
                        }
                    }
                }
            }
        }

        int ans = 0;
        for (int i = 0; i <= 50; i++)
        {
            for (int j = 0; j <= 50; j++)
                ans = (ans + dp[n - 1][i][j]) % MOD;
        }

        return ans % MOD;
    }
};
