int n; vector<int> arr;
const int mod = 1e9+7;
int dp[2001][51][51];

class Solution {
public:
    int rec(int level, int used1, int used2){
        if(level==n){
            return 1;
        }
        if(dp[level][used1][used2]!=-1){
            return dp[level][used1][used2];
        }
        int ans = 0;
        int x = arr[level];
        for(int i=used1; i<=x; i++){
            if(x-i<=used2){
                ans += rec(level+1,i,x-i)%mod;
                ans %= mod;
            }
        }
        return dp[level][used1][used2] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        n = nums.size(); arr = nums;
        memset(dp,-1,sizeof(dp));
        return rec(0,0,50);
    }
};
