class Solution {
    const int mod = 1e9 + 7;
    vector<vector<vector<int>>> dp;
    int rec(vector<int>& nums, int idx, int p1, int p2){
        if(idx == nums.size()) return 1;
        if(dp[idx][p1+1][p2+1] != -1) return dp[idx][p1+1][p2+1];
        int ans = 0;
        for(int v=0;v<=nums[idx];v++){
            if(v>=p1 && (nums[idx]-v)<=p2){
                ans = (ans + 0ll + rec(nums, idx+1, v, nums[idx]-v))%mod;
            }
        }

        return dp[idx][p1+1][p2+1] = ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        dp.assign(nums.size(), vector<vector<int>>(55, vector<int>(55, -1)));
        return rec(nums, 0, -1, 51);
    }
};
