class Solution {
    int mod = 1e9 + 7;
    using ll = long long;
    int dp[2001][51][51];

    ll helper(int index, int maxi, int mini, vector<int>&nums){

        int n = nums.size();

        if(index==n)return 1;

        if(dp[index][maxi][mini]!=-1)return dp[index][maxi][mini];
        ll ans = 0;

        for(int i = mini; i <= nums[index]; i++){

            if((nums[index]-i)<=maxi ){
                ans += helper(index+1, (nums[index]-i), i, nums); 
                ans %= mod;
            }


        }

        return dp[index][maxi][mini] = ans%mod;

    }
public:
    int countOfPairs(vector<int>& nums) {

        memset(dp,-1,sizeof(dp));
       return helper(0, nums[0], 0, nums);
    }
};
