class Solution {

const int MOD = 1000000007;
public:
    int countOfPairs(vector<int>& nums) {
            int n = nums.size();
    int max_num = 50; 
    vector<vector<int>> dp(n, vector<int>(max_num + 1, 0));

    for (int x = 0; x <= nums[0]; ++x) {
        dp[0][x] = 1;
    }

    for (int i = 1; i < n; ++i) {

        vector<int> prefix_sum(max_num + 2, 0);
        for (int y = 0; y <= max_num; ++y) {
            prefix_sum[y + 1] = (prefix_sum[y] + dp[i - 1][y]) % MOD;
        }

        for (int x = 0; x <= nums[i]; ++x) {
            int max_y = min(x, nums[i - 1] + x - nums[i]);
            if (max_y < 0) continue;
            dp[i][x] = prefix_sum[max_y + 1];
        }
    }

    int result = 0;
    for (int x = 0; x <= nums[n - 1]; ++x) {
        result = (result + dp[n - 1][x]) % MOD;
    }

    return result;
    }
};
