class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        M = 10 ** 9 + 7
        N = len(nums)

        @cache
        def go(i, inc):
            if i == N:
                return 1

            num = nums[i]
            dec = nums[i - 1] - inc if i > 0 else inf
            total = 0
            for j in range(inc, num + 1):
                ndec = num - j
                if ndec <= dec:
                    total += go(i + 1, j)
            return total % M

        return go(0, 0) % M
