#include <vector>
using namespace std;

class Solution {
    const int MOD = 1e9 + 7;
    vector<vector<vector<int>>> dp;
    int n;

    int rec(int i, int x, int y, const vector<int>& nums) {
        // base case
        if (i == n) {
            return 1;
        }
        // cache check
        if (dp[i][x][y] != -1) {
            return dp[i][x][y];
        }
        // Transition
        int res = 0;
        for (int x_next = x; x_next <= nums[i]; ++x_next) {
            int y_next = nums[i] - x_next;
            if (y_next <= y) {
                res = (res + rec(i + 1, x_next, y_next, nums)) % MOD;
            }
        }
        // save and return
        return dp[i][x][y] = res;
    }

public:
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        dp.assign(n, vector<vector<int>>(51, vector<int>(51, -1)));

        int result = 0;
        for (int x = 0; x <= nums[0]; ++x) {
            result = (result + rec(1, x, nums[0] - x, nums)) % MOD;
        }

        return result;
    }
};
