#define ll long long
ll mod=1e9+7;
ll dp[2001][52][52];
ll f(int ind,int prev1,int prev2,vector<int>& nums)
{
    int n=nums.size();
    if(dp[ind][prev1][prev2]!=-1) return dp[ind][prev1][prev2];
    if(ind==n) return 1;
    ll cnt=0;
    for(int j=prev1;j<=nums[ind];++j)
    {
        int need=nums[ind]-j;
        if(prev2>=need)
        {
            cnt+=f(ind+1,j,need,nums);
            cnt%=mod;
        }
    }
    return dp[ind][prev1][prev2]=cnt;

}

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return f(0,0,51,nums);
    }
};
