class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def dfs(idx, arr1, arr2):
            if idx == len(nums):
                return 1

            answer = 0
            target = nums[idx]
            for num1 in range(arr1, target+1):
                num2 = target - num1
                if num2 > arr2: continue
                answer = (answer + dfs(idx+1, num1, num2)) % 1000000007
            return answer

        answer = 0
        for num1 in range(nums[0]+1):
            answer = (answer + dfs(1, num1, nums[0]-num1)) % 1000000007
        return answer
